require 'spec_helper.rb'
require File.join(File.dirname(__FILE__), '..', 'lib' ,'gene')


describe Gene do
	context "TP53" do 
		before do
			@gene = Gene.new(
				"",
				"",
				""
			)  
	  	end


	  	it "should find the coding dna at index 190" do
	    	expect(@gene.code_start_index).to eq(190)
	  	end

	  	it "should have coding dna that translates to the proten" do
	  		expect(@gene.coding_dna.translate.chop).to eql(@gene.protein)
	  	end
	end

	context "Incorrect protein string" do 

	  	it "should raise an error that proteins don't match" do
	  		expect{ 
	  			Gene.new(
					"",
					"",
					""
				)
			}.to raise_error(ArgumentError, 'Translated coding dna does not match protein')
	  	end
	end

	context "Missing coding dna" do 
		it "should raise an error that the coding dna can't be found" do
	  		expect{ 
	  			Gene.new(
					"",
					"",
					""
				)
			}.to raise_error(ArgumentError, 'Cannot find coding dna in full dna')
	  	end
	end


end