require File.join(File.dirname(__FILE__), '..', 'lib' ,'mutation')

describe Mutation do
	context "TP53" do 
		before do
			@gene = Gene.new(
				"",
				"",
				""
			)  
	  	end

	  	it "should be reference protein M" do
	  		mutation = Mutation.new(@gene, 1, 'x', 'c')
	  		expect(mutation.reference_protein.translate).to eql 'M'
	  		expect(mutation.reference_protein).to eql 'atg'

	  	end
	  	
	  	it "mutating protein 1 (M), position x to c should give L" do
	  		mutation = Mutation.new(@gene, 1, 'x', 'c')
	  		expect(mutation.mutated_protein.translate).to eql 'L'
	  		expect(mutation.mutated_protein).to eql 'ctg'
	  	end

	  	it "mutating protein 2 (E), position y to t should give V" do
	  		mutation = Mutation.new(@gene, 2, 'y', 't')
	  		expect(mutation.mutated_protein.translate).to eql 'V'
	  		expect(mutation.mutated_protein).to eql 'gtg'
	  	end

	  	it "mutating protein 3 (E), position y to t should give E" do
	  		mutation = Mutation.new(@gene, 3, 'z', 'a')
	  		expect(mutation.mutated_protein.translate).to eql 'E'
	  		expect(mutation.mutated_protein).to eql 'gaa'
	  	end

	  	it "mutating protein 4 (P), position x to t should give S" do
	  		mutation = Mutation.new(@gene, 4, 'x', 't')
	  		expect(mutation.mutated_protein.translate).to eql 'S'
	  		expect(mutation.mutated_protein).to eql 'tcg'
	  	end


	  	it "should raise errors when the protein number (0) is out of range" do
	  		expect {
	  			Mutation.new(@gene, 0, 'x', 'c')
	  		}.to raise_error(ArgumentError, 'Mutated protein number outside of range')
	  	end

	  	it "should raise errors when the protein number (394) is out of range" do
	  		expect {
	  			Mutation.new(@gene, 394, 'x', 'c')
	  		}.to raise_error(ArgumentError, 'Mutated protein number outside of range')
	  	end

	  	it "should raise errors when an invalid subsitution position is given" do
	  		expect {
	  			Mutation.new(@gene, 1, 'p', 'c')
	  		}.to raise_error(ArgumentError, "Subsituted position must be x, y or z")
	  	end

	  	it "should raise errors when an invalid mutated base is given" do
	  		expect {
	  			Mutation.new(@gene, 1, 'x', 'd')
	  		}.to raise_error(ArgumentError, "Subsituted to base must be a, c, t or g")
	  	end

	 end
end
